  SUBROUTINE input_boundary_wrfspec ( fid , grid , config_flags , ierr )
    USE module_io_wrf
    USE module_io
    USE module_domain
    USE module_state_description
    USE module_configure
    USE module_date_time
    USE module_dm
    USE esmf_mod

    IMPLICIT NONE
    LOGICAL wrf_dm_on_monitor, for_out
    EXTERNAL wrf_dm_on_monitor
    TYPE(domain) :: grid
    TYPE(grid_config_rec_type),  INTENT(IN   )    :: config_flags
    INTEGER, INTENT(IN) :: fid 
    INTEGER, INTENT(INOUT) :: ierr
    INTEGER             :: Hndl, io_form

    INTEGER :: ids, ide, jds, jde, kds, kde, ims, ime, jms, jme, kms, kme
    INTEGER :: ips, ipe, jps, jpe, kps, kpe, RC, dim,dim1,dim2,dim3,dim4

    TYPE(ESMF_TimeInterval) :: bdy_increment
    TYPE(ESMF_Time)         :: next_time, current_time
    CHARACTER*40            :: next_datestr,current_datestr

	write(0,*) 'enter input_boundary_wrfspec'


	call get_handle ( Hndl, io_form , for_out, fid )

	write(0,*) 'post get_handle Hndl ', Hndl

!   FIGURE OUT LOOP LIMITS


    CALL get_ijk_from_grid (  grid ,                        &
                              ids, ide, jds, jde, kds, kde,    &
                              ims, ime, jms, jme, kms, kme,    &
                              ips, ipe, jps, jpe, kps, kpe    )

	write(0,*) 'return get_ijk_from_grid'

	write(0,*) 'ids, ide: ', ids, ide
	write(0,*) 'jds, jde: ', jds, jde

	write(0,*) 'bdy_increment, config_flags%bdyfrq: ', bdy_increment, config_flags%bdyfrq

       CALL ESMF_ClockGetCurrTime( grid%domain_clock, current_time, rc=rc )
       CALL ESMF_TimeGetString( current_time, current_datestr, rc=rc )
	write(0,*) 'current_datestr: ', current_datestr(1:19)
	
       CALL ESMF_TimeIntervalSet( bdy_increment, S=NINT(config_flags%bdyfrq),rc=rc)
       next_time = current_time + bdy_increment
       CALL ESMF_TimeGetString( next_time, next_datestr, rc=rc )
	write(0,*) 'next_datestr: ', next_datestr(1:19)

    IF ( wrf_dm_on_monitor() ) THEN

	write(0,*) 'on monitor task....read LBCs'

	read(Hndl) current_datestr(1:19), next_datestr(1:19)
	write(0,*) 'read from wrfbdy current, next: ', &
                     current_datestr(1:19), next_datestr(1:19)

! 2d array
	
	read(Hndl) grid%nmm_pd_b
	read(Hndl) grid%nmm_pd_bt

! 3d array (all same size)
	read(Hndl) grid%nmm_t_b
	read(Hndl) grid%nmm_t_bt

	read(Hndl) grid%nmm_q_b
	read(Hndl) grid%nmm_q_bt

	read(Hndl) grid%nmm_u_b
	read(Hndl) grid%nmm_u_bt

	read(Hndl) grid%nmm_v_b
	read(Hndl) grid%nmm_v_bt

	read(Hndl) grid%nmm_q2_b
	read(Hndl) grid%nmm_q2_bt

	read(Hndl) grid%nmm_cwm_b
	read(Hndl) grid%nmm_cwm_bt

!        CALL atotime( next_datestr(1:19), grid%next_bdy_time )
      write(0,*) 'root task grid%nmm_pd_b(1,1,1,1): ', grid%nmm_pd_b(1,1,1,1)
      write(0,*) 'root task grid%nmm_pd_bt(1,1,1,1): ', grid%nmm_pd_bt(1,1,1,1)
      write(0,*) 'root task grid%nmm_u_b(25,35,1,1): ', grid%nmm_u_b(25,35,1,1)

	ENDIF ! wrf_dm_on_monitor

	dim1=size(grid%nmm_pd_b, dim=1)
	dim2=size(grid%nmm_pd_b, dim=2)
	dim3=size(grid%nmm_pd_b, dim=3)
	dim4=size(grid%nmm_pd_b, dim=4)

	write(0,*) 'dim1,dim2,dim3,dim4: ', dim1,dim2,dim3,dim4

	dim=dim1*dim2*dim3*dim4
	write(0,*) 'call wrf_dm_bcast_bytes for size: ', 4*dim

          CALL wrf_dm_bcast_string(next_datestr(1:19), 19)
	write(0,*) 'everybody next_datestr(1:19): ', next_datestr(1:19)
        CALL atotime( next_datestr(1:19), grid%next_bdy_time )

	write(0,*) 'call wrf_dm_bcast_bytes for size: ', 4*dim
          CALL wrf_dm_bcast_bytes( grid%nmm_pd_b, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_pd_bt, 4*dim )

	write(0,*) 'everybody grid%nmm_pd_b(1,1,1,1): ', grid%nmm_pd_b(1,1,1,1)
	write(0,*) 'everybody grid%nmm_pd_bt(1,1,1,1): ', grid%nmm_pd_bt(1,1,1,1)

	dim1=size(grid%nmm_t_b, dim=1)
	dim2=size(grid%nmm_t_b, dim=2)
	dim3=size(grid%nmm_t_b, dim=3)
	dim4=size(grid%nmm_t_b, dim=4)

	write(0,*) 'for 3D dim1,dim2,dim3,dim4: ', dim1,dim2,dim3,dim4
	dim=dim1*dim2*dim3*dim4
	write(0,*) 'call wrf_dm_bcast_bytes for 3D vars of size: ', 4*dim

          CALL wrf_dm_bcast_bytes( grid%nmm_t_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_t_bt, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_u_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_u_bt, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_v_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_v_bt, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_q2_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_q2_bt, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_cwm_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_cwm_bt, 4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_q_b,  4*dim )
          CALL wrf_dm_bcast_bytes( grid%nmm_q_bt, 4*dim )


  END SUBROUTINE input_boundary_wrfspec

